INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step One Break your lasagna noodles into pieces. There should be a variety of sizes.","0");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Two Color your pasta according to the tutorial above.","1");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Three Cut out two matching sizes of clear contact paper, ours were roughly 10” x 10”.","2");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Four Remove the backing from one piece of contact paper and tape it your work surface using painter’s tae. The sticky side should be facing UP.","3");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Five Have your child place pieces of the colored lasagna on the contact paper. They can custom break pieces as necessary. Leave at least 1/4″ gaps between the pieces. You may want to show them some examples of stained glass to illustrate why.","4");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Six Remove the backing from the second piece of contact paper and gently place it on top of the finished layout. Gently burnish the gaps with your fingertips to seal.","5");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Stained Glass Pasta","  Step Seven Using a wide tip Sharpie, fill in the gap between the colored pieces with black for a stained glass effect.","6");
