INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","","0");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Cover work area with plastic table cover and have paper towels handy to protect against any possible spills.","1");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Wet fabric.","2");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  To create a traditional Itajime pattern, accordion fold or “fan fold\" the fabric in 4 1/2\" wide folds down the length of the fabric. This will form a long rectangle. For more pattern variations, vary the size and width of the folds.","3");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Accordion fold or “fan fold\" the long rectangle into a 4 1/2\" fabric square.","4");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Sandwich the fabric square between the 4\" blocks of wood.","5");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Secure “sandwich\" with rubber bands to tightly hold the fabric square together.","6");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Fill a plastic container with 1.5 gallons of very hot water. The water should ideally be 140ºF. If tap water is not hot enough, heat water on the stove and add to the dye bath.","7");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  To enhance the color add one cup of salt. Add 1 teaspoon of dish detergent to help promote level dyeing. Stir well.","8");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Wearing rubber gloves, add 1/4 cup of well-shaken Indigo dye to dye bath.","9");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Add sandwiched fabric to dye bath. For a high contrast pattern, keep the sandwiched fabric in the dye bath for about 3 to 5 minutes to keep the wrapped part dye-free. For more color and less white areas, leave the wrapped fabric set in the dye bath for 10 to 20 minutes.","10");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  When desired color is achieved, remove wrapped fabric from the dye bath. Do not remove wood blocks.","11");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  We strongly recommend using Rit ColorStay Dye Fixative immediately after dyeing and before washing. It enhances the color, increases color retention and prevents dye from bleeding into white areas. Follow package instructions.","12");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Rinse in cool water until rinse water begins to run clear.","13");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Carefully remove wood blocks and rubber bands. Unfold the fabric.","14");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Shibori Fabric","  Wash in warm water with a mild detergent, rinse and dry.","15");
