INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","","0");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Cover rocks with white acrylic paint and allow to dry.","1");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  In a cup, combine Tulip Glow Dimensional Fabric Paint with Aleene\'s Tacky Glue at a 2:1 ratio.","2");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Add a small amount of water if the mixture is too thick.","3");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Repeat with as many colors as you like. In a single cup, combine the desired colors one at a time.","4");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Prepare a cooling rack and place the dried white rocks on top. Add a piece of watercolor paper below the rack to catch the paint drippings.","5");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Pour the paint on top of the rock, adding multiple layers if desired.","6");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Glow Marbling Rocks","  Dry for 24 hours.","7");
