INSERT INTO Material(Name,Material) VALUES ("Red Heart Fishnet Jars","RED HEART Scrubby");
INSERT INTO Material(Name,Material) VALUES ("Red Heart Fishnet Jars","Smoothie™: 1 ball 9205 Aqua or 9341 Grey");
INSERT INTO Material(Name,Material) VALUES ("Red Heart Fishnet Jars","Mason jar, ruler, scissors, superglue, paperclip (Fringe version only)");
INSERT INTO Material(Name,Material) VALUES ("Red Heart Fishnet Jars","GAUGE: Gauge is not critical for this project.");
INSERT INTO Material(Name,Material) VALUES ("Red Heart Fishnet Jars","JARS");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","1. Measure the height of the jar. Cut 12 lengths of yarn 4 times the height of the jar.","0");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","1");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","2. Cut 1 length of yarn twice the size of the jar mouth circumference.","2");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","3");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","3. Fold the longer lengths in half. Tie each long length evenly across the shorter length, leaving space on both ends to tie to the mouth of the jar.","4");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","5");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","4. Tie the shorter length around the jar mouth and knot securely.","6");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","7");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","5. Starting on one end, tie and knot the long lengths in pairs 1\" (2.54 cm) below the jar mouth. You should have 6 knots total.","8");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","9");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","6. Tie and knot the doubled long lengths in pairs 1\" (2.54 cm) below the last knots, this time tying lengths together to form diamonds all the way around the jar.","10");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","11");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","7. Tie and knot the doubled long lengths in pairs 1\" (2.54 cm) below the last knots, this time tying the strands in pairs as you did in Step 5.","12");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","13");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","8. Repeat steps 6-7, alternating which strands you knot, until you reach the bottom edge of the jar.","14");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","15");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","FINISHING","16");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","17");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","Superglue a yarn tail to the jar mouth, then wrap yarn around the jar several times. Cut and superglue yarn end to secure.","18");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","19");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","Choose one of the following options:","20");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","21");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","Option 1: Fringe","22");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","23");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","When finished knotting around the jar, trim tails to about 1\" (2.5 cm). Use an unfolded paperclip to brush through the fringe ends, breaking apart the plies and making the fringe fluffier. Trim ends as needed.","24");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","25");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","Option 2: Net","26");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","","27");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Red Heart Fishnet Jars","Repeat steps 6-7 to work another round of knots as established. Flip jar upside down and pull all yarn to the center. Knot and superglue yarn ends to the bottom of the jar.","28");
