INSERT INTO Material(Name,Material) VALUES ("Personalized Pencil Holder","  Computer");
INSERT INTO Material(Name,Material) VALUES ("Personalized Pencil Holder","  Polaroid SmartPrep software");
INSERT INTO Material(Name,Material) VALUES ("Personalized Pencil Holder","  STL files (Can be downloaded from the Polaroid 3D model library via the Polaroid SmartPrep software)");
