INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Lay out the fabric making sure it is slightly larger than the hoop. Cut two pieces of ribbon that will be slightly longer than the width of the hoop.","1");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Use Aleene\'s Fabric Fusion to glue the two pieces of ribbon across the fabric.","2");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Place the fabric in the hoop and tighten.","3");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Glue orange pom pom trim to outline the top and bottom of the ribbon.","4");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Print out the Boo template and trim around the word. Turn the paper over and use the chalk or pencil to shade the back over the letters.","5");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Flip the paper over and place it on the ribbon. Use the pencil to trace the word with slight pressure.","6");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Remove the sheet of paper to reveal the light outline of the word.","7");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Trace the outline with the Tulip Slick in black.","8");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Use the Tulip Slick in white to trace the outlines of the ghosts on the fabric.","9");
INSERT INTO Instruction(Name,Instruction,StepNumber) VALUES ("Boo Ghost Hoop","  Let the hoop completely dry before displaying.","10");
